
const prompt = require("prompt-sync")(); 

// Breakfast menu
const Breakfast = {
    1: ["Pancakes", 3500],
    2: ["Omelette", 4000],
    3: ["Tea", 1000],
    4: ["Coffee", 1500]
};

// Lunch menu
const lunch = {
    1: ["Chicken Rice", 6000],
    2: ["Beef Stew", 5500],
    3: ["Chapati", 1000],
    4: ["Juice", 2000]
};

// Ask user to choose a meal
let meal = prompt("Choose a meal (1 for Breakfast, 2 for Lunch):\n ");
let menu = (meal === "1") ? Breakfast : (meal === "2") ? lunch : null;

if (menu) {
    // Display menu items
    for   (let choice in menu) {
        let [item, price] = menu[choice];
        console.log(`${choice}. ${item} - UGX ${price}`);
    }

    // Get user item selections
    let items = prompt("Enter item numbers (e.g. 1,3): ").split(",");

    // Calculate total
    let total = 0;
    for (let choice of items) {
        choice = choice.trim();
        if (!isNaN(choice) & menu[parseInt(choice)]) {
            total += menu[parseInt(choice)][1];
        }
    }

    // Print selected items
    for (let choice of items) {
        choice = choice.trim();
        if (isNaN(choice) & menu[parseInt(choice)]) {
            let [item, price] = menu[parseInt(choice)];
            console.log(`${choice}. ${item} - UGX ${price}`);
        }
    }

    console.log(`\nTotal: UGX ${total}`);
} else {
    console.log("Invalid choice.");
}
